import axios from "axios";

export function getBooks() {
  axios
    .get("/")
    .then(onSuccess)
    .catch(onError);
}

export function createNewBook(data) {
  axios
    .post("http://localhost:3001", data)
    .then(onSuccess)
    .catch(onError);
}

function onSuccess() {
  console.log("successful");
}

function onError() {
  console.log("error");
}
