import React, { Component } from "react";
import { createNewBook, editBook } from "../routes/books";
import DateTimePicker from 'react-datetime-picker';
class BookForm extends Component {
  state = {
    bookEntry: {
      bookName: "",
      totalPages: "",
      currentPage: "",
      dateStarted: ""
    },
    errors: {},
    edited: false
  };

  componentDidMount() {
    if (this.props.location.state !== undefined) {
      return this.handleEdit(this.props.location.state.book);
    }
  }
  handleChange = ({ target }) => {
    const value = target.value;
    const name = target.name;
    this.setState(prevState => ({
      bookEntry: {
        ...prevState.bookEntry,
        [name]: value
      }
    }));
  };

  handleSubmit = (e, bookData) => {
    console.log("submit function", bookData);
    e.preventDefault();
    console.log(bookData, "submit button");
    if (this.state.edited) {
      editBook(this.state.bookEntry);
    } else {
      createNewBook(this.state.bookEntry);
    }
    window.location = "/";
  };

  handleEdit = bookData => {
    this.setState(prevState => ({
      bookEntry: {
        ...prevState.bookEntry,
        _id: bookData._id,
        bookName: bookData.bookName,
        totalPages: bookData.totalPages,
        currentPage: bookData.currentPage,
        dateStarted: bookData.dateStarted
      },
      edited: true
    }));
  };


  render() {
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();
    if (dd < 10) {
      dd = '0' + dd
    }
    if (mm < 10) {
      mm = '0' + mm
    }
    today = yyyy + '-' + mm + '-' + dd;
    console.log(today)

    const { bookEntry } = this.state;
    return (
      <React.Fragment>
        <h1>Book Form</h1>
        <form onSubmit={this.handleSubmit} className="m-2">
          <div className="form-group">
            <label htmlFor="bookName">Book Name</label>
            <input
              type="text"
              className="form-control"
              id="bookName"
              name="bookName"
              aria-describedby="emailHelp"
              placeholder="Book Name"
              value={bookEntry.bookName}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="currentPage">Current Pages</label>
            <input
              type="number"
              className="form-control"
              id="currentPage"
              name="currentPage"
              placeholder="Current Page of Book"
              value={bookEntry.currentPage}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="totalPages">Total Pages</label>
            <input
              type="number"
              className="form-control"
              id="totalPages"
              name="totalPages"
              placeholder="Total Pages of Book"
              value={bookEntry.totalPages}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="dateStarted">Date Started</label>
            {/* <input
              type="date"
              className="form-control"
              id="dateStarted"
              name="dateStarted"
              placeholder="Date Started"
              min="01/01/1900"
              maxLength={today}
              value={bookEntry.dateStarted}
              onChange={this.handleChange}
            /> */}
            <div>
              <DateTimePicker
                className="form-control"
                onChange={this.onChange}
                value={this.state.date}
                maxDate={new Date()}
              />
            </div>
          </div>

          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </React.Fragment>
    );
  }
}

export default BookForm;
