import React, { Component } from "react";
// import CircularProgressbar from "react-circular-progressbar";
import { Line } from "rc-progress";
import "react-circular-progressbar/dist/styles.css";
import { getBooks, removeBook } from "../routes/books";
import BookForm from "./bookForm";
import "../custom.css";
class BookList extends Component {
  state = { bookList: [] };

  componentDidMount() {
    getBooks()
      .then(results => results.data)
      .then(data => this.setState({ bookList: data }));
  }

  handleDelete = book => {
    removeBook(book)
      .then("delete success")
      .catch(err => console.log(err));
  };

  handleEdit = book => {
    console.log(book);
    return <BookForm bookEntry={book} />;
  };
  render() {
    return (
      <React.Fragment>
        <h1>Book List</h1>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Book Name</th>
              <th scope="col">Total Pages</th>
              <th scope="col">Current Page</th>
              <th scope="col">Start Date</th>
              <th scope="col">Progress</th>
              <th scope="col">Completed</th>
            </tr>
          </thead>
          <tbody>
            {this.state.bookList.map((book, index) => (
              <tr key={book._id}>
                <th scope="row">{index + 1}</th>
                <td>{book.bookName}</td>
                <td>{book.totalPages}</td>
                <td>{book.currentPage}</td>
                <td>{book.dateStarted}</td>
                <td>
                  {/* <CircularProgressbar
                    percentage={(book.totalPages - book.currentPage) / 100}
                    text={`${(book.totalPages - book.currentPage) / 100}%`}
                    strokeWidth={4}
                    initialAnimation={true}
                    className="progressBar"
                  /> */}
                  <Line
                    percent={
                      parseFloat(book.totalPages - book.currentPage) / 100
                    }
                    strokeWidth="6"
                    strokeColor="#ff0000"
                    trailColor="#000000"
                  />
                </td>
                <td />
                <td>
                  <button
                    className="btn btn-warning"
                    onClick={() => this.handleEdit(book)}
                  >
                    Edit
                  </button>
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => this.handleDelete(book)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default BookList;
