import React, { Component } from "react";
import { getBooks } from "../routes/books";
class BookForm extends Component {
  state = {
    bookEntry: {
      bookName: "",
      totalPages: "",
      dateStarted: ""
    }
  };

  handleChange = ({ target }) => {
    const value = target.value;
    const name = target.name;
    this.setState(prevState => ({
      bookEntry: {
        ...prevState.bookEntry,
        [name]: value
      }
    }));
  };

  handleSubmit = e => {
    e.preventDefault();
    console.log("submit form", this.state.bookEntry);
    getBooks(this.state.bookEntry);
  };
  render() {
    return (
      <React.Fragment>
        <h1>Book Form</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="bookName">Book Name</label>
            <input
              type="text"
              className="form-control"
              id="bookName"
              name="bookName"
              aria-describedby="emailHelp"
              placeholder="Book Name"
              value={this.state.bookEntry.bookName}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="totalPages">Total Pages</label>
            <input
              type="number"
              className="form-control"
              id="totalPages"
              name="totalPages"
              placeholder="Total Pages of Book"
              value={this.state.bookEntry.totalPages}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="dateStarted">Date Started</label>
            <input
              type="date"
              className="form-control"
              id="dateStarted"
              name="dateStarted"
              placeholder="Date Started"
              value={this.state.bookEntry.dateStarted}
              onChange={this.handleChange}
            />
          </div>

          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </React.Fragment>
    );
  }
}

export default BookForm;
