import React, { Component } from "react";
import Joi from "joi-browser";
import { createNewBook } from "../routes/books";
class BookForm extends Component {
  state = {
    bookEntry: {
      bookName: "",
      totalPages: "",
      dateStarted: ""
    },
    errors: {}
  };

  handleChange = ({ target }) => {
    const errors = { ...this.state.errors };
    const errorMessage = this.validateProperty(target);
    if (errorMessage) errors[target.name] = errorMessage;
    else delete errors[target.name];
    const value = target.value;
    const name = target.name;
    this.setState(prevState => ({
      bookEntry: {
        ...prevState.bookEntry,
        [name]: value
      }
    }));
  };

  handleSubmit = e => {
    e.preventDefault();
    const errors = this.validate();
    this.setState({ errors: errors || {} });
    if (errors) return;
    createNewBook(this.state.bookEntry);
  };

  schema = {
    bookName: Joi.string().required(),
    totalPages: Joi.number()
      .required()
      .min(10)
      .max(999),
    dateStarted: Joi.date().required()
  };

  validate = () => {
    const { error } = Joi.validate(this.state.bookEntry, this.schema);
    if (!error) return null;

    const errors = {};
    for (let item of error.details) errors[item.path[0]] = item.message;
    return errors;
  };

  validateProperty = ({ name, value }) => {
    const obj = { [name]: value };
    const schema = { [name]: this.schema[name] };
    const { error } = Joi.validate(obj, schema);
    return error ? error.details[0].message : null;
  };
  render() {
    return (
      <React.Fragment>
        <h1>Book Form</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="bookName">Book Name</label>
            <input
              type="text"
              className="form-control"
              id="bookName"
              name="bookName"
              aria-describedby="emailHelp"
              placeholder="Book Name"
              value={this.state.bookEntry.bookName}
              onChange={this.handleChange}
              error={this.state.errors[name]}
            />
          </div>
          <div className="form-group">
            <label htmlFor="totalPages">Total Pages</label>
            <input
              type="number"
              className="form-control"
              id="totalPages"
              name="totalPages"
              placeholder="Total Pages of Book"
              value={this.state.bookEntry.totalPages}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="dateStarted">Date Started</label>
            <input
              type="date"
              className="form-control"
              id="dateStarted"
              name="dateStarted"
              placeholder="Date Started"
              value={this.state.bookEntry.dateStarted}
              onChange={this.handleChange}
            />
          </div>

          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </React.Fragment>
    );
  }
}

export default BookForm;
